import React, { useState } from 'react';

function FormExample() {
  const [loginData, setLoginData] = useState({
    username: '',
    password: '',
    isLoggedIn: false
  });
  const [inputValue, setInputValue] = useState('');
  const [errors, setErrors] = useState({});

  const handleLoginChange = (event) => {
    const { name, value } = event.target;
    setLoginData({
      ...loginData,
      [name]: value
    });
  };

  const handleLoginSubmit = (event) => {
    event.preventDefault();
    // Validation logic for login (e.g., checking against database, hardcoded credentials)
    if (loginData.username === 'user' && loginData.password === 'password') {
      setLoginData({
        ...loginData,
        isLoggedIn: true
      });
    } else {
      alert('Invalid username or password');
    }
  };

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const errors = validateFormData();
    if (Object.keys(errors).length === 0) {
      alert('Form submitted with value: ' + inputValue);
    } else {
      setErrors(errors);
    }
  };

  const validateFormData = () => {
    const errors = {};
    if (!inputValue.trim()) {
      errors.input = 'Input is required';
    }
    return errors;
  };

  if (!loginData.isLoggedIn) {
    return (
      <form onSubmit={handleLoginSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input type="text" id="username" name="username" value={loginData.username} onChange={handleLoginChange} />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" name="password" value={loginData.password} onChange={handleLoginChange} />
        </div>
        <button type="submit">Login</button>
      </form>
    );
  }

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={inputValue} onChange={handleChange} />
      {errors.input && <span style={{ color: 'red' }}>{errors.input}</span>}
      <button type="submit">Submit</button>
    </form>
  );
}

export default FormExample;
